import { Link } from "react-router-dom";
import { useState, useEffect } from "react";
import Template from "../components/Template";

export default function App() {

    const [complete, setStatus] = useState(false);

    useEffect(() => {
        getStatus();
    }, []);

    function getStatus() {
        setStatus(true);
    }

    const taskStatus = `button ${complete ? 'task-complete' : ''}`;
  
    return (
        <Template>
          
        <section>
            <h1>Your Tasks</h1>
            <div class="taskList">
                <div class="task">
                    <h3>Task</h3>
                    <h3>task description</h3>
                    <a class="taskStatus" onClick={getStatus}>
                        {complete ? 'Task Complete' : 'Mark as Complete'}
                    </a>
                </div>
                <div class="task">
                    <h3>Task</h3>
                    <p>task description</p>
                </div>
            </div>
        </section>
        <section>
        <div class="button-group-container">
                    <a href="/tasks" class="view-button">All Tasks</a>
                </div>
        </section>
        </Template>
    );
}
